function Open_FieldData_Callback% opens and plots saved data file dir=cd;[filename, pathname]=uigetfile('*.txt; *.*','Data File');if filename~=0	file=strcat(pathname,filename);	fid=fopen(file);	a=fscanf(fid,'%f %f',[2, inf]);	fieldx=a(1,:);	fieldh=a(2,:);	cd(dir);else % user hit 'cancel' button, or other error	fieldx = [];	fieldh = [];	cd(dir);end%put data into structrure so can be accessed by other callbacksdata=get(gcf,'UserData');data.obs.fieldx=fieldx ;data.obs.fieldh=fieldh ;set(gcf,'UserData',data);